package com.ash.ik.string;

import java.util.ArrayList;
import java.util.Arrays;
import java.util.List;

public class FindAnagramIndexes {



    public List<Integer> findAnagrams(String s, String p) {

        List<Integer> out= new ArrayList<>();
        List<String> sorted= new ArrayList<>();

        String word;
        char[] carray;
        int endIndex;
        int splitLen = p.length();
        char[] target=  p.toCharArray();
        Arrays.sort(target);
        String t = String.valueOf(target);
        String tmp;
        for(int i=0; i <= s.length()-splitLen; i++) {
            endIndex = i + splitLen;
            tmp= s.substring(i, endIndex);
            if(!sorted.contains(tmp))  {
                carray = tmp.toCharArray();
                Arrays.sort(carray);
                word= String.valueOf(carray);

                if(t.equals(word)) {
                    out.add(i);
                    sorted.add(tmp);
                }
            } else {
                out.add(i);
            }
        }
        return out;


    }


    public static void main(String[] s) {
        FindAnagramIndexes o = new FindAnagramIndexes();

        String s1 =
            "";
        String p= "yqrbgjdwtcaxzsnifvhmou";
        String s2 =
            "cbaebabacd";
        String p1= "abc";
        String s3 =
            "abab";
        String p2= "ab";
        //o.findAnagrams(s1, p).forEach(System.out::print);
        o.findAnagrams(s2, p1).forEach(System.out::print);
        //o.findAnagrams(s3, p2).forEach(System.out::print);
    }


}
